import React , {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Display from './component/display.js'

class App extends Component {

  state ={

    calculator_result : ''
  }

  render() {

    const {calculator_result} = this.state ;

    return (

      <div>
          <div className="calculator-body">
              <h1> Calculator</h1>
              <Display calculator_result={calculator_result}/>
              <Button calculate={this.calculate}/>
          </div>
      </div>
    )
  }

  calculate = () => {
    const{result} = this.state ;

    this.setState({

      result:''

    })


  }



}

export default App;
