import React , {Component} from 'react';
import ReactDOM from 'react-dom';
import logo from './logo.svg';
import './App.css';
import Display from './component/display.js'
import CalculatorButton from './component/button.js'

class App extends Component {

  state = {
    calculation_result : ''
  }

  buttonClick = value =>{

    if(value === '=')
        this.calculation()
    else if(value === 'CLR')
        this.clear()
    else{
      this.setState({
          calculation_result : this.state.calculation_result + value
      })


    }

  }

  calculation = () => {

      var result = this.state.calculation_result
      this.setState({
        calculation_result:eval(result)

      })


  }

  clear = () =>{

    this.setState({
      calculation_result:''
    })

  }


  render() {

    const {calculation_result} = this.state ;

    return (

      <div className="row">
          <div className="col-md-5"></div>
          <div className="col-md-3">
              <Display calculation_result={calculation_result}/>
              <CalculatorButton  calculate = {this.buttonClick}/>
          </div>
          <div className="col-md-4"></div>
      </div>
    )
  }




}

export default App;
